-- 用户表
CREATE TABLE `user`(
    `uid` INT PRIMARY KEY AUTO_INCREMENT, /*用户编号*/    
    `uname` VARCHAR(32),                  /*用户名-邮箱格式*/
    `phone` VARCHAR(11),                 	/*电话*/
    `upwd` VARCHAR(32),	               	/*密码*/
    `nickname` VARCHAR(32) NOT NULL,	  /*幼儿姓名*/
    `sex`	VARCHAR(1) NOT NULL,		  /*幼儿性别，M-男，F-女, DEFAULT 'M'*/
    `age`	VARCHAR(3) NOT NULL,		  /*幼儿年龄*/
    `class`	VARCHAR(16) NOT NULL	      /*所在班级*/
);

-- 收藏夹
CREATE TABLE `favorite`(
    `fid` INT PRIMARY KEY AUTO_INCREMENT,/*收藏记录id*/
    `userId` INT,         /*用户id*/
    `courseId` INT,       /*首页内容id*/
    `fTime` VARCHAR(16)   /*收藏时间*/
);

-- 购物车表
CREATE TABLE `cart`(
    `ctid` INT PRIMARY KEY AUTO_INCREMENT,/*购物车编号*/
    `userId` INT,         /*用户编号*/
    `courseId` INT,       /*图书编号*/
    `count` INT     /*数量*/
);

-- 订单表
CREATE TABLE `orders`(
    `oid` INT PRIMARY KEY AUTO_INCREMENT,/*订单id*/
    `userId` INT/*用户id*/
);

-- 订单详情表
CREATE TABLE `orders_detail`(
   `odid` INT PRIMARY KEY AUTO_INCREMENT,
   `orderId` INT,                          /*订单编号*/
   `courseId` INT,                       /*图书编号*/
   `count` INT                            /*购买数量*/
);
